        axios.get(`${t06_url}/authors/`, { auth: { username: 'charlotte', password: '12345678' } })
            .then((res) => {
                setAuthors6(res.items)
                authors6.forEach((author) => {
                    axios.get(`${author.id}/posts/`, { auth: { username: 'charlotte', password: '12345678' } })
