import React, { useState, useEffect } from "react";
import SinglePost from "./SinglePost";
import axios from "axios";
import FormData from 'form-data'
import CommentList from './../Comment/CommentList'
import './PostList.css'
import { jSXNamespacedName } from "@babel/types";


const base_url = process.env.REACT_APP_CURRENT_URL;
const t06_url = process.env.REACT_APP_API_URL;
const t07_url = process.env.REACT_APP_API2_URL;


function PostList({ handleShare }) {
    const AUTHOR_ID = "1111111111";
    const [t06Id, sett06Id] = useState('');
    const [t07Id, sett07Id] = useState('');
    const [postData, setPostData] = useState([]);
    const [authors, setAuthors] = useState([]);
    const [authors6, setAuthors6] = useState([]);
    const [authors7, setAuthors7] = useState([]);
    let t06Data = new FormData();
    t06Data.append("displayName", "charlotte")
    t06Data.append("password", "12345678")
    let t07Data = new FormData();

    useEffect(() => {
        axios
            .get(`${base_url}/authors/`)
            .then((res) => {
                setAuthors(res.data.items)
            })
            .catch((e) => console.log(e));

        let temp = []
        authors.map((author) => {
            axios
                .get(`${author.id}/posts/`)
                .then((res) => {
                    res.data.forEach((post) => {
                        temp.push(post)
                    })
                })
                .catch((e) => console.log(e));
        })
        setPostData(temp)
        /*
        axios
            .get(`${t06_url}/authors/`, { auth: { username: 'charlotte', password: '12345678' } })
            .then((data) => {
                setAuthors6(data.data.items)
                authors6.map((author) => {
                    return (
                        axios
                            .get(`${author.id}/posts/`, { auth: { username: 'charlotte', password: '12345678' } })
                            .then((res) => {
                                setPostData(res.data.items)
                            })
                            .catch((e) => console.log(e))
                    )
                })
            })
            .catch((e) => console.log(e));
        */
    }, [postData, authors])

    return (<div>
        <ul className="PostList">
            {postData.map((val, key) => {
                return (
                    <li key={key} id="onePost">
                        <SinglePost
                            author={val.author}
                            postId={val.id}
                            description={val.description}
                            comments={<CommentList postId={val.id} />}
                            handleShare={handleShare}
                        />
                    </li>
                );
            })}</ul>
    </div>
    )
}

export default PostList;
